package main

import "fmt"

var (
	markerSize = 14

	line = ""
)

func main() {
	buffer := make(map[byte]int)
	for i := 0; i < markerSize-1; i++ {
		buffer[line[i]]++
	}
	for i := markerSize - 1; i < len(line); i++ {
		buffer[line[i]]++
		if isMarker(buffer) {
			fmt.Println("n characters:", i+1)
			break
		}
		buffer[line[i-(markerSize-1)]]--
	}
}

func isMarker(buffer map[byte]int) bool {
	for _, count := range buffer {
		if count > 1 {
			return false
		}
	}
	return true
}
